INSERT INTO tb_descricao_detalhe (id, version_id, co_detalhe, ds_detalhe, dt_competencia) VALUES (1, 1, '001', 'DESTINA-SE A IDENTIFICAR SE NO VALOR DO PROCEDIMENTO JÁ ESTÁ INCLUSO QUALQUER TIPO DE ANESTESIA, EXCETO ANESTESIA LOCAL, POIS ESTA JÁ ESTÁ INCLUSA NO VALOR DO PROCEDIMENTO. ||NOS PROCEDIMENTOS HOSPITALARES COM O ATRIBUTO "INCLUI ANESTESIA" SIGNIFICA QUE O VALOR DA ANESTESIA ESTÁ INCLUÍDO NO COMPONENTE SP (SERVIÇOS PROFISSIONAIS), NÃO PERMITINDO O REGISTRO DE CÓDIGOS DE ANESTESIA EM SEPARADO, DO PROCEDIMENTO PRINCIPAL. NOS PROCEDIMENTOS AMBULATORIAIS, COM O ATRIBUTO "INCLUI ANESTESIA" SIGNIFICA QUE O VALOR DA ANESTESIA ESTÁ INCLUÍDO NO COMPONENTE SA (SERVIÇO AMBULATORIAL). ||', '202504');
